"use strict";


class RetMessage {
    constructor() {

        console.log(' RetMessage -->constructor start.');
        this.retObj = {
            errorCode:0,
            errorMessage:'',
            data:{},
        };

    };

    setErrorCode(errorCode)
    {
        this.retObj.errorCode = errorCode;
    }

    getErrorCode()
    {
        return this.retObj.errorCode ;
    }

    setErrorMsg(errorMsg)
    {
        this.retObj.errorMessage = errorMsg;
    }

    getErrorMsg()
    {
        return this.retObj.errorMessage ;
    }

    setErrorInfo(errorCode,errorMsg)
    {
        this.retObj.errorCode = errorCode;
        this.retObj.errorMessage = errorMsg;
    }

    setData(data)
    {
        this.retObj.data = data;
    }

    getData()
    {
        return this.retObj.data ;
    }

    getRetObj()
    {
        return this.retObj;
    }

}

module.exports = RetMessage;