 class TimeLogger{
    constructor() { //构造函数

    }

    startTime()
    {
        this.startTm = process.hrtime();
    }
    endTime(title) {
        let _endAt0 = process.hrtime();
        let _startAt0 = this.startTm;
        let ms = ((_endAt0[0] - _startAt0[0]) * 1e3 + (_endAt0[1] - _startAt0[1]) * 1e-6).toFixed(3);
        console.log( `${title} time: ${ms} ms`);
    }
}

var timeLogger = new TimeLogger();
exports.log = timeLogger;
exports.TimeLogger = TimeLogger;